tool.command:
cp -R '${input_genomes_storage.extra_files_path}' '${output_genomes_storage.extra_files_path}' && mkdir '${output_dir.extra_files_path}'
&& anvi-pan-genome
#if $output_genomes_storage: --genomes-storage '${output_genomes_storage.extra_files_path}/${output_genomes_storage.metadata.anvio_basename}' #end if
#if $str( $genome_names_source.genome_names_source_selector ) == "file": #if $genome_names_source.genome_names: --genome-names '${genome_names_source.genome_names}' #end if #else: #if $str( $genome_names_source.genome_names ): --genome-names '${genome_names_source.genome_names}' #end if #end if
${skip_alignments}
${skip_homogeneity}
${quick_homogeneity}
#if $str( $align_with ): --align-with '${align_with}' #end if
${exclude_partial_gene_calls}
${use_ncbi_blast}
#if $str( $minbit ): --minbit '${minbit}' #end if
#if $str( $mcl_inflation ): --mcl-inflation '${mcl_inflation}' #end if
#if $str( $min_occurrence ): --min-occurrence '${min_occurrence}' #end if
#if $str( $min_percent_identity ): --min-percent-identity '${min_percent_identity}' #end if
${sensitive}
#if $str( $project_name ): --project-name '${project_name}' #end if
#if $description: --description '${description}' #end if
#if $output_dir: --output-dir '${output_dir.extra_files_path}/' #end if
${overwrite_output_destinations}
--num-threads "\${GALAXY_SLOTS:-1}"
${skip_hierarchical_clustering}
${enforce_hierarchical_clustering}
#if $str( $distance ): --distance '${distance}' #end if
#if $str( $linkage ): --linkage '${linkage}' #end if
&> '${GALAXY_ANVIO_LOG}'

citations.citation:
